mapper.update:
update setmeal
category_id=#{categoryId}, name=#{name}, price=#{price}, status=#{status}, description=#{description}, image=#{image}, create_time=#{createTime}, update_time=#{updateTime}, update_user=#{updateUser}, create_user=#{createUser}
where id=#{id}

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.select:
select setmeal.id,setmeal.category_id,setmeal.name,setmeal.price,setmeal.status,setmeal.description,setmeal.image,setmeal.update_time,category.name
from setmeal join category on setmeal.category_id = category.id

    and category_id=#{categoryId}
     and setmeal.name like concat('%',#{name},'%')
    and setmeal.status=#{status}
